import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css';


const Navbar = () => {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true)
  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false)
 
  const showButton = () => {
    if(window.innerWidth <=960){
      setButton(false);
    } else{
      setButton(true);
    }
  };

  useEffect(()=>{
    showButton(); }, []
    );



  window.addEventListener('resize',showButton);

  return (
    <>
    
        <nav className = "navbar">
            <div className = "navbar-container">
                <Link to="/" className = "navbar-logo" onClick = {closeMobileMenu}>
                    DATA VIEWER <i class="fa-brands fa-github"></i>
                </Link>
                <div className = 'menu-icon' onClick={handleClick}>
                  <i className = {click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className = {click ? 'nav-menu active' :'nav-menu'}>
                  
                  <li className ='nav-item'>
                    <Link to='/pie' className = 'nav-links' onClick={closeMobileMenu}>
                      Pie Chart
                    </Link>
                  </li>
                  <li className ='nav-item'>
                    <Link to='/bar' className = 'nav-links' onClick={closeMobileMenu}>
                      Bar Chart
                    </Link>
                  </li>
                  <li className ='nav-item'>
                    <Link to='/line' className = 'nav-links' onClick={closeMobileMenu}>
                      Line Chart
                    </Link>
                  </li>
                  
                  <li className ='nav-item'>
                    <Link to='/sign-up' className = 'nav-links-mobile' onClick={closeMobileMenu}>
                      Sign Up
                    </Link>
                  </li>

                </ul>
                {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}
            </div>
        </nav>
    
    
    </>
  )
}

export default Navbar
